mapper.select:
SELECT STSEQ, CID, CNAME, PIC0, PIC1, PIC2, PIC3, PIC4, PIC5, PIC6, PIC7, PIC8, PIC9, VIDEO, CONTENT, OPENTIME, CLOSETIME, ADDRE FROM ( SELECT ROW_NUMBER() OVER(ORDER BY STSEQ DESC) AS RNUM, STSEQ, CID, CNAME, PIC0, PIC1, PIC2, PIC3, PIC4, PIC5, PIC6, PIC7, PIC8, PIC9, VIDEO, CONTENT, OPENTIME, CLOSETIME, ADDRE FROM WD_STUDIO WHERE 1=1
AND CNAME LIKE '%'||#{s_keyword}||'%'
AND CONTENT LIKE '%'||#{s_keyword}||'%'
) A WHERE RNUM BETWEEN ${start} AND ${end}

SELECT STSEQ, CID, CNAME, PIC0, PIC1, PIC2, PIC3, PIC4, PIC5, PIC6, PIC7, PIC8, PIC9, VIDEO, CONTENT, OPENTIME, CLOSETIME, ADDRE
FROM WD_STUDIO
WHERE STSEQ=#{stseq}

SELECT SDPDSEQ, SDSEQ, TITLE, CONTENT, PRICE
FROM WD_SD_PRODUCT
WHERE SDSEQ=#{stseq}

SELECT PRICE
FROM WD_SD_PRODUCT
WHERE SDSEQ=${sdseq} AND SDPDSEQ=${sdpdseq}

mapper.update:
UPDATE WD_STUDIO
SET PIC0=#{pic[0]}, PIC1=#{pic[1]}, PIC2=#{pic[2]}, PIC3=#{pic[3]}, PIC4=#{pic[4]},
	PIC5=#{pic[5]}, PIC6=#{pic[6]}, PIC7=#{pic[7]}, PIC8=#{pic[8]}, PIC9=#{pic[9]}, 
	CONTENT=#{content}, OPENTIME=#{opentime}, CLOSETIME=#{closetime}
WHERE STSEQ=#{stseq}
